import React from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import '../styles/Header.css';

function Header({ isLoggedIn, user, onLogout }) {
  const location = useLocation();
  const isMainPage = location.pathname === "/";
  const navigate = useNavigate();

  // Ïñ¥ÎìúÎØºÏù¥ ÏïÑÎãå ÏÇ¨Ïö©ÏûêÍ∞Ä /adminÏóê Ï†ëÍ∑º Ïãú Î©îÏù∏ÏúºÎ°ú Î¶¨Îã§Ïù¥Î†âÌä∏
  React.useEffect(() => {
    if (location.pathname === "/admin" && (!user || user.user_role !== "Admin")) {
      navigate("/", { replace: true });
    }
  }, [location.pathname, user, navigate]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    if (onLogout) onLogout();
    navigate('/login');
  };

  return (
    <header className="header">
      <div className="logo">
        <Link to="/">üé¨Î¨¥ÎπÑÌèâ</Link>
      </div>
      {!isMainPage && (
        <div className="search-box">
          <input
            type="text"
            placeholder="ÏòÅÌôî Ï†úÎ™© Í≤ÄÏÉâ"
          />
        </div>
      )}
      <div className="user-menu">
        {isLoggedIn ? (
          <>
            <span>{user?.nickname || user?.username}</span>
            {user.user_role === "Admin" && (
              <button onClick={() => navigate("/admin")}>Ïñ¥ÎìúÎØº</button>
            )}
            <button onClick={handleLogout}>Î°úÍ∑∏ÏïÑÏõÉ</button>
          </>
        ) : (
          <>
            <Link to="/login" className='isLoggedIn'>Î°úÍ∑∏Ïù∏</Link>
            <Link to="/register" className='isLoggedIn'>ÌöåÏõêÍ∞ÄÏûÖ</Link>
          </>
        )}
      </div>
    </header>
  );
}

export default Header;
