/**
 * 주소 : /commons/Main.js
 * 설명 : 메인 페이지
 * 작성자 : 신주안
 * 작성일 : 2025.05.15
 * 수정일 : 
 * 기능 : 영화 사이트의 메인 화면
 *      - 인기 영화 섹션
 *          - 현재 인기 있는 영화 리스트 출력 (포스터, 제목, 출시년도, 제작 국가, 평균 별점)
 *          - 영화 클릭 시 해당 영화의 상세 페이지로 이동
 *      - 검색 기능
 *          - 검색창에 영화 제목 입력 시 관련 영화 리스트 출력
 *          - 검색 결과 클릭 시 해당 영화의 상세 페이지로 이동
 *          - 검색창 백그라운드에 슬라이드형 이미지(최신 영화 포스터) 출력
 *          - 우측 화살표 버튼 : 클릭 시 다음 이미지로 슬라이드
 *          - 좌측 화살표 버튼 : 클릭 시 이전 이미지로 슬라이드
 *      - 태그 기능
 *        - 태그 클릭 시 해당 태그에 맞는 영화 리스트 출력
 *      - 로그인 상태 확인
 *          - 로그인 상태에 따라 상단 메뉴(로그인/로그아웃 버튼) 변경
 *            - 로그인 상태 : 사용자 ID, 로그아웃 버튼
 *                - 로그아웃 버튼 클릭 시 로그아웃 요청
 *                - 로그아웃 성공 시 메인 페이지로 이동
 *            - 비로그인 상태 : 로그인, 회원가입 버튼
 *                - 로그인 버튼 클릭 시 로그인 페이지로 이동
 *                - 회원가입 버튼 클릭 시 회원가입 페이지로 이동
 *                - 
 * 백 요청 :
 *      - /api/search/boxoffice       # movies -> search , popular -> boxoffice
 *          - 박스오피스 순위 리스트 요청
 *          - 영화 제목 검색 요청
 *          - 사용자 로그인 상태 확인 요청
 */

import React, { useState, useEffect } from "react";
import Header from "../components/Header";
import MovieSection from "../components/MovieSection";
import BannerSlider from "../components/BannerSlider";

function MainPage({ isLoggedIn, user, onLogout }) {
    const [movies, setMovies] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState("");

    useEffect(() => {
        const fetchMovies = async () => {
            setLoading(true);
            setError("");
            try {
                // 박스오피스 순위로 영화 목록 가져오기
                const res = await fetch("/api/search/boxoffice");
                const data = await res.json();
                setMovies(data.movies || []);
            } catch (err) {
                setError("영화 목록을 불러오지 못했습니다.");
            }
            setLoading(false);
        };
        fetchMovies();
    }, []);

    const bannerImages = movies.slice(0, 5).map((movie) => movie.poster_url);

    return (
        <div className="w-full overflow-x-hidden">
            <Header isLoggedIn={isLoggedIn} user={user} onLogout={onLogout} />

            {/* TMDB 포스터 path 조합 예시 */}
            <div style={{ background: '#222', color: 'white', padding: '1rem', margin: '1rem 0', borderRadius: 8 }}>
                <h3>포스터/배경 이미지 URL 조합 예시</h3>
                <div style={{ display: 'flex', gap: 24, alignItems: 'center' }}>
                    <div>
                        <div style={{ fontSize: 14, marginBottom: 4 }}>포스터(w200)</div>
                        <img src={movies[0]?.poster_url ? `https://image.tmdb.org/t/p/w200${movies[0].poster_url}` : ''} alt="포스터" style={{ width: 100, borderRadius: 6, background: '#444' }} />
                    </div>
                    <div>
                        <div style={{ fontSize: 14, marginBottom: 4 }}>배경(w780)</div>
                        <img src={movies[0]?.poster_url ? `https://image.tmdb.org/t/p/w780${movies[0].poster_url}` : ''} alt="배경" style={{ width: 200, borderRadius: 6, background: '#444' }} />
                    </div>
                    <div style={{ fontSize: 13, color: '#aaa' }}>
                        <div>DB poster_url: <br />{movies[0]?.poster_url || '(없음)'}</div>
                        <div style={{ marginTop: 8 }}>포스터 URL: <br />{movies[0]?.poster_url ? `https://image.tmdb.org/t/p/w200${movies[0].poster_url}` : '(없음)'}</div>
                        <div style={{ marginTop: 8 }}>배경 URL: <br />{movies[0]?.poster_url ? `https://image.tmdb.org/t/p/w780${movies[0].poster_url}` : '(없음)'}</div>
                    </div>
                </div>
            </div>

            <div>
                <BannerSlider images={bannerImages} />
                <div className="search-overlay">
                    <h1 className="search-title">어떤 영화를 찾으십니까?</h1>
                    <input 
                        type="text"
                        placeholder="Search"
                        className="search-input"
                    />
                    <p className="search-description">
                        장르 또는 태그로 탐색해보세요.
                    </p>
                    <div className="tag-list" style={{
                        marginTop: "1rem",
                        display: "flex",
                        flexWrap: "wrap",
                        gap: "0.5rem",
                        justifyContent: "center",
                    }}>
                        {["액션", "범죄", "SF", "코미디", "로맨스", "스릴러", "공포", "드라마", "멜로/로맨스", "미스터리", "판타지", "누아르", "역사", "전쟁", "재난", "애니메이션", "오피스", "요리"].map((tag) => (
                            <button 
                            key={tag}
                            style={{
                                padding: "0.4rem 0.8rem",
                                borderRadius: "9999px",
                                background: "transparent",
                                color: "white",
                                border: "1px solid white",
                                cursor: "pointer",
                                fontSize: "0.9rem",
                            }}
                            >
                                {tag}
                            </button>
                        ))}
                    </div>
                </div>
            </div>
            <h2 className="section-title">인기 영화</h2>
            {loading ? (
                <div style={{ color: 'white', textAlign: 'center', margin: '2rem' }}>로딩 중...</div>
            ) : error ? (
                <div style={{ color: '#ff6464', textAlign: 'center', margin: '2rem' }}>{error}</div>
            ) : (
                <MovieSection movies={
                    movies
                        .slice() // 원본 배열 보호
                        .sort((a, b) => {
                            if (a.rank && b.rank) return a.rank - b.rank;
                            if (a.rank) return -1;
                            if (b.rank) return 1;
                            return 0;
                        })
                        .map(m => ({
                            id: m.tmdb_id,
                            posterUrl: m.poster_url,
                            title: m.title,
                            releaseDate: m.release_date,
                            country: m.country,
                            genre: m.genreAlt || ""
                        }))
                } />
            )}
        </div>
    );
};

export default MainPage;
