import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import MainPage from "./commons/Main";
import LoginPage from "./commons/Login";
import RegisterPage from "./commons/Register";
import { jwtDecode } from 'jwt-decode';
import Header from './components/Header';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [user, setUser] = useState({});

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      try {
        const decoded = jwtDecode(token);
        setIsLoggedIn(true);
        setUser(decoded);
      } catch (e) {
        setIsLoggedIn(false);
        setUser({});
      }
    } else {
      setIsLoggedIn(false);
      setUser({});
    }
  }, []);

  return (
    <Router>
      <Header isLoggedIn={isLoggedIn} user={user} />
      <Routes>
        <Route path="/" element={<MainPage isLoggedIn={isLoggedIn} user={user} />} />
        <Route path="/login" element={<LoginPage isLoggedIn={isLoggedIn} user={user} />} />
        <Route path="/register" element={<RegisterPage isLoggedIn={isLoggedIn} user={user} />} />
      </Routes>
    </Router>
  );
}

export default App;
