import React from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import '../styles/Header.css';

function Header() {
  const location = useLocation();
  const isMainPage = location.pathname === "/";
  const navigate = useNavigate();

  // localStorageÏóêÏÑú ÌÜ†ÌÅ∞ ÌôïÏù∏
  const token = localStorage.getItem('token');
  const isLoggedIn = !!token;

  // (ÏÑ†ÌÉù) ÌÜ†ÌÅ∞ÏóêÏÑú ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Î≥µÏõê (Ïã§Ï†ú ÏÑúÎπÑÏä§ÏóêÏÑúÎäî ÌÜ†ÌÅ∞ ÎîîÏΩîÎî© ÎòêÎäî /me API ÌïÑÏöî)
  let user = null;
  if (isLoggedIn) {
    // Í∞ÑÎã®Ìûà usernameÎßå Î≥µÏõê (Ïã§Ï†ú ÏÑúÎπÑÏä§ÏóêÏÑúÎäî jwt-decode Îì± ÏÇ¨Ïö© Í∂åÏû•)
    user = { username: 'Î°úÍ∑∏Ïù∏Îê®' };
  }

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  return (
    <header className="header">
      <div className="logo">
        <Link to="/">üé¨Î¨¥ÎπÑÌèâ</Link>
      </div>
      {!isMainPage && (
        <div className="search-box">
          <input
            type="text"
            placeholder="ÏòÅÌôî Ï†úÎ™© Í≤ÄÏÉâ"
          />
        </div>
      )}
      <div className="user-menu">
        {isLoggedIn ? (
          <>
            <span>{user.username}</span>
            <button onClick={handleLogout}>Î°úÍ∑∏ÏïÑÏõÉ</button>
          </>
        ) : (
          <>
            <Link to="/login" className='isLoggedIn'>Î°úÍ∑∏Ïù∏</Link>
            <Link to="/register" className='isLoggedIn'>ÌöåÏõêÍ∞ÄÏûÖ</Link>
          </>
        )}
      </div>
    </header>
  );
}

export default Header;
